ldings)
  buildings.sort_by { |build| build[0] }
  group, curGroup=[], []
  curSize=nil
  buildings.each do |build|
    if curSize==nil
      curGroup<<build
      curSize=[build[0], build[1]]
    elsif curSize!=nil && curSize[1]<build[0]
      group<<curGroup
      curGroup=[build]
      curSize=[build[0], build[1]]
    else
      curGroup<<build
      curSize[1] =build[1] if build[1]>curSize[1]
    end
  end
  group<<curGroup
  group
end

def getKeyPoints(group)
  buildings=group.sort_by { |build| build[2] }.reverse
  simplified=[]
  buildings.each do |build|
    if simplified.size==0
      simplified<<build
    else
      neoIntervs=[build]
      simplified.each_with_index do |higher, higherIndex|
        tmp=[]
        neoIntervs.each do |curInterv|
          len=higher[0]>curInterv[0] ? (higher[1]-curInterv[0]) : (curInterv[1]-higher[0])
          if len > higher[1]-higher[0]+curInterv[1]-curInterv[0]
            tmp<<curInterv
          elsif higher[2]==curInterv[2]
            tmpLeft, tmpRight=[curInterv[0], higher[0]].min, [curInterv[1], higher[1]].max
            simplified[higherIndex]=[tmpLeft, tmpRight, higher[2]]
          else
            left=[curInterv[0], higher[0], curInterv[2]] if curInterv[0]<higher[0]
            right=[higher[1], curInterv[1], curInterv[2]] if higher[1]<curInterv[1]
            tmp<<left if left!=nil
            tmp<<right if right!=nil
          end
        end
        neoIntervs=tmp
      end
      simplified+=neoIntervs
    end
  end
  keyPoints=[]
  simplified.sort_by! { |build| build[0] }.each { |build| keyPoints<<[build[0], build[2]] }
  keyPoints<<[simplified[-1][1], 0]
end

def get_skyline(buildings)
  return [] if buildings.size==0
  #return 'err' if buildings.size>1499
  groups=get_group(buildings)
  keyPoints=[]
  groups.each { |group| keyPoints<<getKeyPoints(group) }
  keyPoints.inject(:+)
end

